import React from "react"
import { Row, Col } from "reactstrap"
import Breadcrumbs from "../../components/@vuexy/breadCrumbs/BreadCrumb"
import DataTableEmployees from "./DataTableEmployees"
import EmployeesAdd from "./EmployeesAdd"


class EmployeesPage extends React.Component {
  constructor(props){
      super(props);
      this.state = {
          addPage: false,
      }
  }

  changeViewPage = async(typeView) =>{
    if (typeView == 'add') {
        await this.setState({addPage: true})
    }
  }
  render() {
    return (
      <React.Fragment>
        <Breadcrumbs
          breadCrumbTitle="Employees"
        //   breadCrumbParent="Employees"
          breadCrumbActive="Employees"
        />
        <Row>
          <Col sm="12">
              {this.state.addPage ? 
                <EmployeesAdd {...this.state} {...this.props}/>
              : 
                <DataTableEmployees {...this.state} {...this.props} changeViewPage={this.changeViewPage}/>
              }
          </Col>
        </Row>
      </React.Fragment>
    )
  }
}

export default EmployeesPage
