class DataTableCustom extends React.Component {
  state = {
    columns: [
      {
        name: "Name",
        selector: "name",
        sortable: true,
        minWidth: "200px",
        cell: row => (
          <div className="d-flex flex-xl-row flex-column align-items-xl-center align-items-start py-xl-0 py-1">
            <div className="user-img ml-xl-0 ml-2">
              {/* <img
                className="img-fluid rounded-circle"
                height="36"
                width="36"
                src={row.image}
                alt={row.name}
              /> */}
            </div>
            <div className="user-info text-truncate ml-xl-50 ml-0">
              <span
                // title={row.name}
                title={row.first_name+' '+row.last_name}
                className="d-block text-bold-500 text-truncate mb-0">
                {/* {row.name} */}
                {row.first_name+' '+row.last_name}
              </span>
              {/* <small title={row.email}>{row.email}</small> */}
              <small title={row.emp_no}>{row.emp_no}</small>
            </div>
          </div>
        )
      },
    ],
    data: [
      {
        image: require("../../../assets/img/portrait/small/avatar-s-12.jpg"),
        name: "Hortensia Soaper",
        email: "hsoaperh@mapy.cz",
        date: "June 1, 2017",
        status: "active",
        revenue: "$60,000",
        ratings: "good"
      }
    ],
    filteredData: [],
    value: ""
  }

  componentDidMount=async()=>{
    console.log('table custom : ')
    // var result = await axios.get('https://jsonplaceholder.typicode.com/users')
    //                         .then(result => {
    //                             let res = result.data
    //                             console.log('result : ', result)
    //                             if (res.length == '') {
    //                               return []
    //                             }
    //                             // console.log('result : ', (result.data))
    //                             return res;
    //                         }).catch(err => {
    //                             return []
    //                         })


      let sql = `
        select * from employees limit 100;
      `
      var result = await axios.post('http://localhost:3001/api/query', {
                                query : sql
                              })
                                .then(result => {
                                  let res = result.data.response
                                  console.log('result : ', result)
                                  if (res.length == '') {
                                    return []
                                  }
                                  // console.log('result : ', (result.data))
                                  return res;
                              }).catch(err => {
                                console.log('error : ', err)
                                  return []
                              })



    // await this.setState({
    //   data: [
    //     {
    //       image: require("../../../assets/img/portrait/small/avatar-s-12.jpg"),
    //       name: "Hortensia Soaper",
