class OpSsCreate extends React.Component {
  constructor(props){
    super(props);
    this.state = {
      fotoKondisiSebelum: img2,
      ssTest: '',
      myProfile:null,
      img: img2,
      step: 0,
      isChecked: false,
      ssPartner: null,
