      sweetAlertTextConfirm: 'Text',
      sweetAlertTypeConfirm: 'warning',
      dataAtasan: [],
      dataPartner: [],
      cffNik: '',
      cffName: '',
      cffKondisiSaatIni: '',
      cffKondisiDiusulkan: '',
      cffAlasanPerubahan: '',
      cffDampakPerubahan: '',
      ssAtasanSelected: null,
      ssTema: '',
      ssLokasi: '',
      ssKondisiSebelum: '',
      ssKondisiSesudah: '',
      ssPermasalahan: '',
      ssImprovement: '',
      ssBiaya: 0,
      ssBiayaUraian: '',
      ssKeuntungan: 0,
      ssKeuntunganUraian: '',

