export const login = (state = { userRole: "admin" }, action) => {
  switch (action.type) {
    // case "LOGIN_WITH_EMAIL": {
    //   return { ...state, values: action.payload }
    // }
    // case "LOGIN_WITH_FB": {
    //   return { ...state, values: action.payload }
    // }
    // case "LOGIN_WITH_TWITTER": {
    //   return { ...state, values: action.payload }
    // }
    // case "LOGIN_WITH_GOOGLE": {
    //   return { ...state, values: action.payload }
    // }
    // case "LOGIN_WITH_GITHUB": {
    //   return { ...state, values: action.payload }
    // }
    // case "LOGIN_WITH_JWT": {
    //   return { ...state, values: action.payload }
    // }
    case "LOGIN_WITH_CUSTOM": {
      state.userRole = action.payload.role
      console.log('custom reducer : ', state)
      console.log('custom reducer : ', action)
      localStorage.setItem('account', JSON.stringify(action.payload))
      // sessionStorage.setItem('account', JSON.stringify(action.payload))
      return { ...state, values: action.payload }
    }
    case "LOGOUT_WITH_CUSTOM": {
      window.localStorage.clear();
      // sessionStorage.clear();
      return { ...state, values: action.payload }
    }
    // case "LOGOUT_WITH_JWT": {
    //   return { ...state, values: action.payload }
    // }
    // case "LOGOUT_WITH_FIREBASE": {
    //   return { ...state, values: action.payload }
    // }
    // case "CHANGE_ROLE": {
    //   return { ...state, userRole: action.userRole }
    // }
    default: {
      return state
    }
  }
}
