import React from "react"
import { Card, CardBody, CardHeader, CardTitle } from "reactstrap"
import DataTable from "react-data-table-component"

const columns = [
  {
    name: "Id",
    selector: "id",
    sortable: true
  },
  {
    name: "First Name",
    selector: "first_name",
    sortable: true
  },
  {
    name: "Last Name",
    selector: "last_name",
    sortable: true
  },
  {
    name: "Email",
    selector: "email",
    sortable: true
  },
  {
    name: "Gender",
    selector: "gender",
    sortable: true
  }
]

const data = [
  {
    id: 1,
