      myurl2 = await storageRef2.getDownloadURL().catch((error)=>{throw error});
      console.log('myurl : ', myurl2)
    }

    if (!this.state.isCheckedFoto) {
      myurl = ''
      myurl2 = ''
    }


      //news
      // let sql = `
      //   UPDATE df_suggestion_system
      //   SET tema='`+this.state.ssTema+`', 
