const sweetAlert = (props) => {
    return (
        <SweetAlert warning title={props.title}
          show={props.warningAlert} 
          onConfirm={() => props.handleAlert("isSweetAlert", false)}
        >
            <p className={props.className}>
              {props.text}
            </p>
        </SweetAlert>
    )
}

