import React from "react"
import {
  Button,
  Modal,
  ModalHeader,
  ModalBody,
  ModalFooter,
} from "reactstrap"

class ModalDigimon extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            modal: false
            // modal: false
        };
    }

        toggleModal = () => {
            // this.setState(state => ({
            // modal: !this.state.modal
            // }))
            this.setState({
                modal: !this.state.modal
            })
        }

  render(){
    return(
        <>
        {/* <Button
            color="primary"
            className="btn-block"
            size="lg"
            outline
            onClick={this.toggleModal}
            >
            Launch Modal
        </Button> */}
        <Modal
            isOpen={this.state.modal}
            toggle={this.toggleModal}
            className="modal-dialog-centered"
            >
            <ModalHeader toggle={this.toggleModal}>
                Vertically Centered
            </ModalHeader>
            <ModalBody className="modal-dialog-centered">
                Oat cake ice cream candy chocolate cake chocolate cake
                cotton candy drag√©e apple pie. Brownie carrot cake candy
                canes bonbon fruitcake topping halvah. Cake sweet roll cake
                cheesecake cookie chocolate cake liquorice.
            </ModalBody>
            <ModalFooter>
                <Button color="primary" onClick={this.toggleModal}>
                Accept
                </Button>{" "}
            </ModalFooter>
        </Modal>
    </>
    );
 }
}
export default ModalDigimon
