import React, { useState } from "react"
import { api_query } from "../../../../api/ApiConstant"
import axios from 'axios'
import {
  Card,
  CardHeader,
  CardTitle,
  CardBody,
  FormGroup,
  Row,
  Col,
  Input,
  Form,
  Button,
  Label, Modal, ModalHeader, ModalBody, ModalFooter, Alert
} from "reactstrap"
import Checkbox from "../../../../components/@vuexy/checkbox/CheckboxesVuexy"
import { Check, Star, Save, Send, X } from "react-feather"
import { history } from "../../../../../src/history"
import Select from "react-select"
import SweetAlert from "react-bootstrap-sweetalert"
import Toggle from "react-toggle"

import "react-toggle/style.css"
import "../../../../assets/scss/plugins/forms/switch/react-toggle.scss"
import Spinner from "../../../../components/@vuexy/spinner/Fallback-spinner"


class SpvApprovalSsView extends React.Component {
  constructor(props){
    super(props);
    this.state = {
      isLoading: false,
      myProfile:null,
      step: 0,
      modal: false,
      modalReject: false,
      alasanReject: '',
      dataKategori: [],
      kategoriSelected: null,
      isChecked: false,
      catatanCC: '',
      isSweetAlert: false,
      sweetAlertTitle: 'Judul',
      sweetAlertText: 'Text',
      sweetAlertType: 'warning',
      isSweetAlertConfirm: false,
      sweetAlertTitleConfirm: 'Judul',
      sweetAlertTextConfirm: 'Text',
      sweetAlertTypeConfirm: 'warning',
      dataAtasan: [],
      cffNik: '',
      cffName: '',
      cffNamaDepartemen: '',
      cffKondisiSaatIni: '',
      cffKondisiDiusulkan: '',
      cffAlasanPerubahan: '',
      cffDampakPerubahan: '',
      // ssAtasanSelected: null,
      // ssTema: '',
      // ssLokasi: '',
      // ssKondisiSebelum: '',
      // ssKondisiSesudah: '',
      // ssPermasalahan: '',
      // ssImprovement: '',
      // ssBiaya: 0,
      // ssBiayaUraian: '',
      // ssKeuntungan: 0,
      // ssKeuntunganUraian: '',
      /////////
      // cffNik: '',
      // cffName: '',
      // cffKondisiSaatIni: this.props.editData.kondisi_sekarang,
      // cffKondisiDiusulkan: this.props.editData.kondisi_diusulkan,
      // cffAlasanPerubahan: this.props.editData.alasan_perubahan,
      // cffDampakPerubahan: this.props.editData.dampak_perubahan,
      ssAtasanSelected: this.props.editData.fasilitator_id,
      ssTema: this.props.editData.tema,
      ssLokasi: this.props.editData.lokasi,
      ssKondisiSebelum: this.props.editData.kondisi_sebelum,
      ssKondisiSesudah: this.props.editData.kondisi_sesudah,
      ssPermasalahan: this.props.editData.permasalahan,
      ssImprovement: this.props.editData.improvement,
      ssBiaya: parseInt(this.props.editData.biaya),
      ssBiayaUraian: this.props.editData.biaya_uraian,
      ssKeuntungan: parseInt(this.props.editData.keuntungan),
      ssKeuntunganUraian: this.props.editData.keuntungan_uraian,
    }
}

componentDidMount = async() => {
  await this.setState({isLoading: true})
  await this.isLogin('spv_df');
  await this.tarikDataProfile();
  // await this.tarikDataFormCff();
  await this.tarikKategori();
  await this.setState({isLoading: false})
  // await this.tarikAtasan();

  console.log('props edit : ', this.props)
}

toggleModal = () => {
  this.setState(prevState => ({
    modal: !prevState.modal
  }))
}

toggleModalFix = async() => {
  this.setState(prevState => ({
    modal: !prevState.modal
  }))

  if (this.state.kategoriSelected == '' || this.state.kategoriSelected == null) {return await this.handleAlert('isSweetAlert', true, 'Warning', 'Kategori Tidak Boleh Kosong', 'warning')}
  else{
      if(this.state.isChecked){
        if (this.state.catatanCC == '' || this.state.catatanCC == null) {return await this.handleAlert('isSweetAlert', true, 'Warning', 'No CC Tidak Boleh Kosong', 'warning')}
      }
  }

  if (this.state.isChecked) {
    console.log('isChecked')
    // let sql = `
    //     with ins1 as(
    //         update df_cff
    //         set
    //         change_control = 'Y',
    //         catatan_cc = '`+this.state.catatanCC+`',
    //         status_cff_id = 3,
    //         update_by = `+this.state.myProfile.id+`,
    //         update_date = now()
    //         where id = `+this.props.editData.cff_id+`
    //     returning id as return_id
    //     )
    //         update df_ss
    //         set
    //         ss_kategori_id = `+this.state.kategoriSelected+`,
    //         ss_status_id = 3,
    //         update_by = `+this.state.myProfile.id+`,
    //         update_date = now()
    //         where id = `+this.props.editData.ds_id+`
    //     `

    let sql = `
            update df_suggestion_system
            set
            change_control = 'Y',
            nomor_cc = '`+this.state.catatanCC+`',
            ss_kategori_id = `+this.state.kategoriSelected+`,
            ss_status_id = 3,
            update_by = `+this.state.myProfile.id+`,
            update_date = now()
            where id = `+this.props.editData.ds_id+`
        `
        console.log('sql : ', sql)

        var result = await axios.post(api_query, {
                                    query : sql
                                })
                                    .then(async(result) => {
                                    let res = result.data.response
                                    // Beda dgn sweetAlert yg lain
                                    return await this.handleAlertConfirm('isSweetAlertConfirm', true, 'Success', 'Berhasil Approve SS', 'success')
                                    // this.props.changeViewPage('page')
                                }).catch(async(err) => {
                                    console.log('error : ', err)
                                    return await this.handleAlertConfirm('isSweetAlertConfirm', true, 'Danger', 'Gagal Approve SS', 'danger')
                                    // return []
                                })

  }else{
    console.log('not Checked')
    // let sql = `
    //     with ins1 as(
    //         update df_cff
    //         set
    //         change_control = 'N',
    //         status_cff_id = 3,
    //         update_by = `+this.state.myProfile.id+`,
    //         update_date = now()
    //         where id = `+this.props.editData.cff_id+`
    //     returning id as return_id
    //     )
    //         update df_ss
    //         set
    //         ss_kategori_id = `+this.state.kategoriSelected+`,
    //         ss_status_id = 3,
    //         update_by = `+this.state.myProfile.id+`,
    //         update_date = now()
    //         where id = `+this.props.editData.ds_id+`
    //     `

    let sql = `
              update df_suggestion_system
              set
              change_control = 'N',
              ss_kategori_id = `+this.state.kategoriSelected+`,
              ss_status_id = 3,
              update_by = `+this.state.myProfile.id+`,
              update_date = now()
              where id = `+this.props.editData.ds_id+`
          `
        console.log('sql : ', sql)

        var result = await axios.post(api_query, {
                                    query : sql
                                })
                                    .then(async(result) => {
                                    let res = result.data.response
                                    // Beda dgn sweetAlert yg lain
                                    return await this.handleAlertConfirm('isSweetAlertConfirm', true, 'Success', 'Berhasil Approve SS', 'success')
                                    // this.props.changeViewPage('page')
                                }).catch(async(err) => {
                                    console.log('error : ', err)
                                    return await this.handleAlertConfirm('isSweetAlertConfirm', true, 'Danger', 'Gagal Approve SS', 'danger')
                                    // return []
                                })
  }

}

toggleModalReject = () => {
  this.setState(prevState => ({
    modalReject: !prevState.modalReject
  }))
}

toggleModalRejectFix = async() => {
  this.setState(prevState => ({
    modalReject: !prevState.modalReject
  }))

  if (this.state.alasanReject == '' || this.state.alasanReject == null) {return await this.handleAlert('isSweetAlert', true, 'Warning', 'Alasan Reject Tidak Boleh Kosong', 'warning')}
  else{
    // let sql = `
    //     with ins1 as(
    //         update df_cff
    //         set
    //         status_cff_id = 8,
    //         update_by = `+this.state.myProfile.id+`,
    //         update_date = now()
    //         where id = `+this.props.editData.cff_id+`
    //     returning id as return_id
    //     )
    //         update df_ss
    //         set
    //         catatan_reject_delete = '`+this.state.alasanReject+`',
    //         ss_status_id = 8,
    //         update_by = `+this.state.myProfile.id+`,
    //         update_date = now()
    //         where id = `+this.props.editData.ds_id+`
    //     `

        let sql = `
            update df_suggestion_system
            set
            catatan_reject_delete = '`+this.state.alasanReject+`',
            ss_status_id = 8,
            update_by = `+this.state.myProfile.id+`,
            update_date = now()
            where id = `+this.props.editData.ds_id+`
        `
        console.log('sql : ', sql)

        var result = await axios.post(api_query, {
                                    query : sql
                                })
                                    .then(async(result) => {
                                    let res = result.data.response
                                    // Beda dgn sweetAlert yg lain
                                    return await this.handleAlertConfirm('isSweetAlertConfirm', true, 'Success', 'Berhasil Reject SS', 'success')
                                    // this.props.changeViewPage('page')
                                }).catch(async(err) => {
                                    console.log('error : ', err)
                                    return await this.handleAlertConfirm('isSweetAlertConfirm', true, 'Danger', 'Gagal Reject SS', 'danger')
                                    // return []
                                })

      }
}

isLogin = async(role) =>{
  if (localStorage.getItem('account') == undefined || localStorage.getItem('account') == null) {
    history.push("/pages/login")
  }else if(JSON.parse(localStorage.getItem('account')).role !== role){
    history.push("/pages/login")
  }
  else{
    var myProfile = JSON.parse(localStorage.getItem('account'))
    console.log('myProfile ss : ', myProfile)
    this.setState({myProfile: myProfile})
  }
}

tarikAtasan = async(departement_id) =>{



  let sql = `
  select id as value,
    name as label,
    departement_id
  from df_master_user dmu
  where departement_id = `+departement_id+`
  and job_level = '4'
  `

  var result = await axios.post(api_query, {
                              query : sql
                          })
                              .then(async(result) => {
                                var res = result.data.response.rows
                                console.log('result query atasan : ', res)
                                if (res.length == '') {
                                    return []
                                }

                                await this.setState({dataAtasan: res})

                              }).catch(err => {
                                  console.log('error : ', err)
                                  return []
                              })
    // await this.setState({data: result})
  }

tarikKategori = async() =>{

let sql = `
  select  id as value,
          nama_kategori as label
  from df_ss_kategori
`

var result = await axios.post(api_query, {
                            query : sql
                        })
                            .then(async(result) => {
                              var res = result.data.response.rows
                              console.log('result query kategori : ', res)
                              if (res.length == '') {
                                  return []
                              }

                              await this.setState({dataKategori: res})

                            }).catch(err => {
                                console.log('error : ', err)
                                return []
                            })
  // await this.setState({data: result})
}

tarikDataProfile = async() =>{
  let sql = `
      select nik, name, departement_id, nama_departemen, job_level from df_master_user dmu
      join df_master_departement dmd
      on dmu.departement_id = dmd.id
      where dmu.id = `+this.props.editData.pengusul_id+`
  `
  var result = await axios.post(api_query, {
                              query : sql
                          })
                              .then(async(result) => {
                                var res = result.data.response.rows[0]
                                console.log('result query profile : ', res)
                                if (res.length == '') {
                                    return
                                }else{

                                  await this.tarikAtasan(res.departement_id);
                                  await this.setState({
                                    cffNik: res.nik,
                                    cffName: res.name,
                                    cffNamaDepartemen: res.nama_departemen,
                                  })
                                }

                                // await this.setState({dataAtasan: res})

                              }).catch(err => {
                                  console.log('error : ', err)
                                  return []
                              })
    // await this.setState({data: result})
}

tarikDataFormCff = async() =>{
  let sql = `
      select * from df_cff dc where dc.id = `+this.props.editData.cff_id+`
  `
  var result = await axios.post(api_query, {
                              query : sql
                          })
                              .then(async(result) => {
                                var res = result.data.response.rows[0]
                                console.log('result query cff : ', res)
                                if (res.length == '') {
                                    return
                                }else{
                                  await this.setState({
                                    cffKondisiSaatIni: res.kondisi_sekarang,
                                    cffKondisiDiusulkan: res.kondisi_diusulkan,
                                    cffAlasanPerubahan: res.alasan_perubahan,
                                    cffDampakPerubahan: res.dampak_perubahan,
                                  })
                                }

                                // await this.setState({dataAtasan: res})

                              }).catch(err => {
                                  console.log('error : ', err)
                                  return []
                              })
    // await this.setState({data: result})
}

handleAlertConfirm = (state, value, title, text, type) => {

  if (value) {
    this.setState({ [state] : value })
    this.setState({sweetAlertTitleConfirm: title, sweetAlertTextConfirm: text, sweetAlertTypeConfirm: type});
  }else{
    this.setState({ [state] : value })
    this.props.changeViewPage('page')
}
  // if(text == "Berhasil membuat SS"){
  //   this.setState({ [state] : value })
  //   this.setState({sweetAlertTitle: title, sweetAlertText: text, sweetAlertType: type});
  //   // this.props.changeViewPage('page')
  // }else{}
}

handleAlert = (state, value, title, text, type) => {
    if (value) {
      this.setState({ [state] : value })
      this.setState({sweetAlertTitle: title, sweetAlertText: text, sweetAlertType: type});
    }else{
      this.setState({ [state] : value })
    }
}


saveForm = async() =>{

  var title = ''
  var text = ''
  var type = ''
  // console.log('SUBMIT : ', this.state.first_name)
  // console.log('SUBMIT : ', this.state.last_name)
  // console.log('event : ', e)


  if (this.state.cffKondisiSaatIni == '' || this.state.cffKondisiSaatIni == null) {return await this.handleAlert('isSweetAlert', true, 'Warning', 'CFF Kondisi Saat Ini Tidak Boleh Kosong', 'warning')}
  if (this.state.cffKondisiDiusulkan == '' || this.state.cffKondisiDiusulkan == null) {return await this.handleAlert('isSweetAlert', true, 'Warning', 'CFF Kondisi Diusulkan Tidak Boleh Kosong', 'warning')}
  if (this.state.cffAlasanPerubahan == '' || this.state.cffAlasanPerubahan == null) {return await this.handleAlert('isSweetAlert', true, 'Warning', 'CFF Alasan Perubahan Tidak Boleh Kosong', 'warning')}
  if (this.state.cffDampakPerubahan == '' || this.state.cffDampakPerubahan == null) {return await this.handleAlert('isSweetAlert', true, 'Warning', 'CFF Dampak Perubahan Tidak Boleh Kosong', 'warning')}
  if (this.state.ssAtasanSelected == '' || this.state.ssAtasanSelected == null) {return await this.handleAlert('isSweetAlert', true, 'Warning', 'Fasilitator Tidak Boleh Kosong', 'warning')}
  if (this.state.ssTema == '' || this.state.ssTema == null) {return await this.handleAlert('isSweetAlert', true, 'Warning', 'Tema Tidak Boleh Kosong', 'warning')}
  if (this.state.ssLokasi == '' || this.state.ssLokasi == null) {return await this.handleAlert('isSweetAlert', true, 'Warning', 'Lokasi Tidak Boleh Kosong', 'warning')}
  if (this.state.ssKondisiSebelum == '' || this.state.ssKondisiSebelum == null) {return await this.handleAlert('isSweetAlert', true, 'Warning', 'Kondisi Sebelum Tidak Boleh Kosong', 'warning')}
  if (this.state.ssKondisiSesudah == '' || this.state.ssKondisiSesudah == null) {return await this.handleAlert('isSweetAlert', true, 'Warning', 'Kondisi Sesudah Tidak Boleh Kosong', 'warning')}
  if (this.state.ssPermasalahan == '' || this.state.ssPermasalahan == null) {return await this.handleAlert('isSweetAlert', true, 'Warning', 'Permasalahan Tidak Boleh Kosong', 'warning')}
  if (this.state.ssImprovement == '' || this.state.ssImprovement == null) {return await this.handleAlert('isSweetAlert', true, 'Warning', 'Improvement Tidak Boleh Kosong', 'warning')}
  if (this.state.ssBiayaUraian == '' || this.state.ssBiayaUraian == null) {return await this.handleAlert('isSweetAlert', true, 'Warning', 'Biaya Uraian Tidak Boleh Kosong', 'warning')}
  if (this.state.ssBiaya == '' || this.state.ssBiaya == null) {return await this.handleAlert('isSweetAlert', true, 'Warning', 'Biaya Tidak Boleh Kosong', 'warning')}
  if (this.state.ssKeuntunganUraian == '' || this.state.ssKeuntunganUraian == null) {return await this.handleAlert('isSweetAlert', true, 'Warning', 'Keuntungan Uraian Tidak Boleh Kosong', 'warning')}
  if (this.state.ssKeuntungan == '' || this.state.ssKeuntungan == null) {return await this.handleAlert('isSweetAlert', true, 'Warning', 'Keuntungan Tidak Boleh Kosong', 'warning')}
  else {

      // let sql = `
      //     with ins1 as (insert into df_cff(pengusul_id, kondisi_sekarang, kondisi_diusulkan, alasan_perubahan, dampak_perubahan, status_cff_id, create_by, update_by)
      //     values(`+this.state.myProfile.id+`, '`+this.state.cffKondisiSaatIni+`', '`+this.state.cffKondisiDiusulkan+`', '`+this.state.cffAlasanPerubahan+`', '`+this.state.cffDampakPerubahan+`', 2, `+this.state.myProfile.id+`, `+this.state.myProfile.id+`)
      //     returning id as return_id)

      //     insert into df_ss(tema, pengusul_id, fasilitator_id, lokasi, kondisi_sebelum, kondisi_sesudah, permasalahan, improvement, biaya, biaya_uraian, keuntungan, keuntungan_uraian, ss_status_id, cff_id, create_by, update_by)
      //     values('`+this.state.ssTema+`', `+this.state.myProfile.id+`, '`+this.state.ssAtasanSelected+`', '`+this.state.ssLokasi+`', '`+this.state.ssKondisiSebelum+`', '`+this.state.ssKondisiSesudah+`', '`+this.state.ssPermasalahan+`', '`+this.state.ssImprovement+`', `+this.state.ssBiaya+`, '`+this.state.ssBiayaUraian+`', `+this.state.ssKeuntungan+`, '`+this.state.ssKeuntunganUraian+`', 2, (select return_id from ins1), `+this.state.myProfile.id+`, `+this.state.myProfile.id+`)
      // `

      // jika kirim status = 2
      let sql = `
      with ins1 as(
          update df_cff
          set
          pengusul_id = `+this.state.myProfile.id+`,
          kondisi_sekarang = '`+this.state.cffKondisiSaatIni+`',
          kondisi_diusulkan = '`+this.state.cffKondisiDiusulkan+`',
          alasan_perubahan = '`+this.state.cffAlasanPerubahan+`',
          dampak_perubahan = '`+this.state.cffDampakPerubahan+`',
          status_cff_id = 1,
          update_by = `+this.state.myProfile.id+`,
          update_date = now()
          where id = `+this.props.editData.cff_id+`
      returning id as return_id
      )
          update df_ss
          set
          tema = '`+this.state.ssTema+`',
          pengusul_id = `+this.state.myProfile.id+`,
          fasilitator_id = '`+this.state.ssAtasanSelected+`',
          lokasi = '`+this.state.ssLokasi+`',
          kondisi_sebelum = '`+this.state.ssKondisiSebelum+`',
          kondisi_sesudah = '`+this.state.ssKondisiSesudah+`',
          permasalahan = '`+this.state.ssPermasalahan+`',
          improvement = '`+this.state.ssImprovement+`',
          biaya = `+this.state.ssBiaya+`,
          biaya_uraian = '`+this.state.ssBiayaUraian+`',
          keuntungan = `+this.state.ssKeuntungan+`,
          keuntungan_uraian = '`+this.state.ssKeuntunganUraian+`',
          ss_status_id = 1,
          update_by = `+this.state.myProfile.id+`,
          update_date = now()
          where id = `+this.props.editData.ds_id+`
      `
      console.log('sql : ', sql)

      var result = await axios.post(api_query, {
                                  query : sql
                              })
                                  .then(async(result) => {
                                  let res = result.data.response
                                  // Beda dgn sweetAlert yg lain
                                  return await this.handleAlertConfirm('isSweetAlertConfirm', true, 'Success', 'Berhasil edit SS', 'success')
                                  // this.props.changeViewPage('page')
                              }).catch(async(err) => {
                                  console.log('error : ', err)
                                  return await this.handleAlertConfirm('isSweetAlertConfirm', true, 'Danger', 'Gagal edit SS', 'danger')
                                  // return []
                              })

      // this.setState({isLoading: true});
      // console.log('result query : ', result)
      // this.props.changeViewPage('page')
      // // return await this.handleAlert('isSweetAlert', true, 'Success', 'Data Berhasil Ditambahkan '+this.state.first_name+ " "+ this.state.last_name, 'success')
  }

}

