                    <Label for="nik">NIK</Label>
                    <Input
                      type="text"
                      name="nik"
                      id="nik"
                      placeholder="NIK"
                      // value={this.state.myProfile == null ? '':this.state.myProfile.nik}
                      value={this.state.cffNik}
                      disabled
                    />
                  </FormGroup>
                </Col>
                <Col sm="12">
                  <FormGroup>
                    <Label for="name">Name</Label>
                    <Input
                      type="text"
                      name="name"
                      id="name"
