import React from "react"
import { api_query } from "../../../../api/ApiConstant"
import axios from 'axios'
import {
  Card,
  CardHeader,
  CardTitle,
  CardBody,
  FormGroup,
  Row,
  Col,
  Input,
  Form,
  Button,
  Label
} from "reactstrap"

import Checkbox from "../../../../components/@vuexy/checkbox/CheckboxesVuexy"
import { Check, Star, Save, Send } from "react-feather"
import { history } from "../../../../../src/history"
import Select from "react-select"
import SweetAlert from "react-bootstrap-sweetalert"

class OpSsCreate extends React.Component {
  constructor(props){
    super(props);
    this.state = {
      myProfile:null,
      step: 0,
      isSweetAlert: false,
      sweetAlertTitle: 'Judul',
      sweetAlertText: 'Text',
      sweetAlertType: 'warning',
      dataAtasan: [],
      cffNik: '',
      cffName: '',
      cffKondisiSaatIni: '',
      cffKondisiDiusulkan: '',
      cffAlasanPerubahan: '',
      cffDampakPerubahan: '',
      ssAtasanSelected: null,
      ssTema: '',
      ssLokasi: '',
      ssKondisiSebelum: '',
      ssKondisiSesudah: '',
      ssPermasalahan: '',
      ssImprovement: '',
      ssBiaya: 0,
      ssBiayaUraian: '',
      ssKeuntungan: 0,
      ssKeuntunganUraian: '',

    }
}

componentDidMount = async() => {
  await this.isLogin('op_df');
  await this.tarikAtasan();
}

isLogin = async(role) =>{
  if (localStorage.getItem('account') == undefined || localStorage.getItem('account') == null) {
    history.push("/pages/login")
  }else if(JSON.parse(localStorage.getItem('account')).role !== role){
    history.push("/pages/login")
  }
  else{
    var myProfile = JSON.parse(localStorage.getItem('account'))
    console.log('myProfile ss : ', myProfile)
    this.setState({myProfile: myProfile})
  }
}


tarikAtasan = async() =>{



  let sql = `
  select id as value,
    name as label,
    departement_id
  from df_master_user dmu
  where departement_id = `+this.state.myProfile.departement_id+`
  and job_level = '4'
  `

  var result = await axios.post(api_query, {
                              query : sql
                          })
                              .then(async(result) => {
                                var res = result.data.response.rows
                                console.log('result query atasan : ', res)
                                if (res.length == '') {
                                    return []
                                }

                                await this.setState({dataAtasan: res})

                              }).catch(err => {
                                  console.log('error : ', err)
                                  return []
                              })
    // await this.setState({data: result})
  }

handleAlert = (state, value, title, text, type) => {
    if (value) {
      this.setState({ [state] : value })
      this.setState({sweetAlertTitle: title, sweetAlertText: text, sweetAlertType: type});
    }else{
      this.setState({ [state] : value })
    }

}

submitForm = async() =>{

  var title = ''
  var text = ''
  var type = ''
  // console.log('SUBMIT : ', this.state.first_name)
  // console.log('SUBMIT : ', this.state.last_name)
  // console.log('event : ', e)


  if (this.state.first_name == '' || this.state.first_name == null) {return await this.handleAlert('isSweetAlert', true, 'Warning', 'Nama Awalan Tidak Boleh Kosong', 'warning')}
  if (this.state.last_name == '' || this.state.last_name == null) {return await this.handleAlert('isSweetAlert', true, 'Warning', 'Nama Akhiran Tidak Boleh Kosong', 'warning')}
  if (this.state.emp_no == '' || this.state.last_name == null) {return await this.handleAlert('isSweetAlert', true, 'Warning', 'No Karyawan Tidak Boleh Kosong', 'warning')}
  if (this.state.gender == '' || this.state.gender == null) {return await this.handleAlert('isSweetAlert', true, 'Warning', 'Jenis Kelamin Tidak Boleh Kosong', 'warning')}
  if (this.state.dept_name_selected == '' || this.state.dept_name_selected == null) {return await this.handleAlert('isSweetAlert', true, 'Warning', 'Departement Tidak Boleh Kosong', 'warning')}
  if (this.state.position_selected == '' || this.state.position_selected == null) {return await this.handleAlert('isSweetAlert', true, 'Warning', 'Position Tidak Boleh Kosong', 'warning')}
  if (this.state.profileImage == '' || this.state.profileImage == null) {return await this.handleAlert('isSweetAlert', true, 'Warning', 'Foto Profile Tidak Boleh Kosong', 'warning')}
  else {

    this.setState({isLoading: true});

    // var bucketName = 'images'
    // var file = this.state.profileImage[0]
    // var storageRef = await firebase.storage().ref(`${bucketName}/${file.name}`)
    // let uploadTask = await storageRef.put(file)
    // var myurl = await storageRef.getDownloadURL().catch((error)=>{throw error});
    // console.log('myurl : ', myurl)

      let sql = `
          insert into employees_new(first_name, last_name, emp_no, gender, image_profile)
          value('`+this.state.first_name+`', '`+this.state.last_name+`', `+this.state.emp_no+`, '`+this.state.gender+`', '`+myurl+`');
      `
      var result = await axios.post(api_query, {
                                  query : sql
                              })
                                  .then(async(result) => {
                                  let res = result.data.response
                                  // console.log('result : ', result)

                                  // if (res.length == '') {
                                  //     return []
                                  // }

                                  // console.log('result insert : ', (result.data))
                                  // return res;

                                  let sql = `
                                    select LAST_INSERT_ID() as return_id;
                                  `
                                  var result = await axios.post(api_query, {
                                      query : sql
                                  })
                                    .then(async(result) => {
                                      // console.log('return first : ', result.data.response[0])
                                      console.log('return : ', result.data.response[0].return_id)
                                      let res = result.data.response[0].return_id
                                      // return res;

                                      let sql2 = `
                                          insert into dept_emp_new(emp_id, dept_id, position_id)
                                          value(`+res+`, `+this.state.dept_name_selected+`, `+this.state.position_selected+`);
                                      `

                                      var result2 = await axios.post(api_query, {
                                                                    query : sql2
                                                                  })
                                                                      .then(async(result) => {
                                                                        let res = result.data.response
                                                                        console.log('result : ', result)

                                                                        if (res.length == '') {
                                                                            return []
                                                                        }

                                                                        // console.log('result insert : ', (result.data))
                                                                        return res;

                                                                  }).catch(err => {
                                                                      console.log('error : ', err)
                                                                      return []
                                                                  })

                                    }).catch(err => {
                                      console.log('error : ', err)
                                      return []
                                  })

                              }).catch(err => {
                                  console.log('error : ', err)
                                  return []
                              })

      this.setState({isLoading: true});
      console.log('result query : ', result)
      this.props.changeViewPage('page')
      // return await this.handleAlert('isSweetAlert', true, 'Success', 'Data Berhasil Ditambahkan '+this.state.first_name+ " "+ this.state.last_name, 'success')
  }

}

  render() {
    return (
      <>
      {this.state.step == 0 ?
      <Card>
        <CardHeader>
          <CardTitle>CFF Form</CardTitle>
        </CardHeader>
        <CardBody>
          <Form>
            <Row>
              <Col sm="12">
                <FormGroup>
                  <Label for="nik">NIK</Label>
                  <Input
                    type="text"
                    name="nik"
                    id="nik"
                    placeholder="NIK"
                    value={this.state.myProfile == null ? '':this.state.myProfile.nik}
                    disabled
                  />
                </FormGroup>
              </Col>
              <Col sm="12">
                <FormGroup>
                  <Label for="name">Name</Label>
                  <Input
                    type="text"
                    name="name"
                    id="name"
                    placeholder="Name"
                    value={this.state.myProfile == null ? '':this.state.myProfile.name}
                    disabled
                  />
                </FormGroup>
              </Col>
              <Col sm="12">
                <FormGroup>
                  <Label for="departement">Departement</Label>
                  <Input
                    type="text"
                    name="departement"
                    id="departement"
                    placeholder="Departement"
                    value={this.state.myProfile == null ? '':this.state.myProfile.nama_departemen}
                    disabled
                  />
                </FormGroup>
              </Col>
              <Col sm="12">
                <FormGroup>
                  <Label for="">Kondisi Saat Ini</Label>
                  <Input
                    type="textarea"
                    rows="3"
                    id='kondisiSaatIni'
                    onChange={(val)=>this.setState({cffKondisiSaatIni: val.target.value})}
                    value={this.state.cffKondisiSaatIni}
                    placeholder={'Jelaskan kondisi saat ini'}
                    required
                    />
                </FormGroup>
              </Col>
              <Col sm="12">
                <FormGroup>
                  <Label for="">Kondisi yang diusulkan</Label>
                  <Input
                    type="textarea"
                    rows="3"
                    id='kondisiDiusulkan'
                    onChange={(val)=>this.setState({cffKondisiDiusulkan: val.target.value})}
                    value={this.state.cffKondisiDiusulkan}
                    placeholder={'Jelaskan kondisi yang diusulkan'}
                    required
                    />
                </FormGroup>
              </Col>
              <Col sm="12">
                <FormGroup>
                  <Label for="">Alasan Perubahan</Label>
                  <Input
                    type="textarea"
                    rows="3"
                    id='alasanPerubahan'
                    onChange={(val)=>this.setState({cffAlasanPerubahan: val.target.value})}
                    value={this.state.cffAlasanPerubahan}
                    placeholder={'Jelaskan alasan perubahan'}
                    required
                    />
                </FormGroup>
              </Col>
              <Col sm="12">
                <FormGroup>
                  <Label for="">Dampak Perubahan</Label>
                  <Input
                    type="textarea"
                    rows="3"
                    id='dampakPerubahan'
                    onChange={(val)=>this.setState({cffDampakPerubahan: val.target.value})}
                    value={this.state.cffDampakPerubahan}
                    placeholder={'Jelaskan dampak perubahan'}
                    required
                    />
                </FormGroup>
              </Col>

              {/* <Col sm="12">
                <FormGroup>
                  <Label for="">VerticalPassword</Label>
                  <Input
                    type="password"
                    name="password"
                    id="passwordVertical"
                    placeholder="Password"
                  />
                </FormGroup>
              </Col>
              <Col sm="12">
                <FormGroup>
                  <Label for="EmailVertical">Email</Label>
                  <Input
                    type="email"
                    name="Email"
                    id="EmailVertical"
                    placeholder="Email"
                  />
                </FormGroup>
              </Col> */}
              {/* <Col sm="12">
                <FormGroup>
                  <Checkbox
                    color="primary"
                    icon={<Check className="vx-icon" size={16} />}
                    label="Remember Me"
                    defaultChecked={false}
                  />
                </FormGroup>
              </Col> */}
              <Col sm="12">
                <FormGroup>
                  {/* <Button.Ripple
                    color="primary"
                    type="submit"
                    className="mr-1 mb-1"
                    // onClick={this.setState({step: 1})}
                    onClick={e => e.preventDefault()}
                  >
                    Submit next
                  </Button.Ripple>
                  <Button.Ripple
                    outline
                    color="warning"
                    type="reset"
                    className="mb-1"
                  >
                    Reset
                  </Button.Ripple> */}
                  <div className="d-flex flex-wrap justify-content-end">
                    {/* <Button.Ripple color="none" className="bg-gradient-warning" onClick={() => this.setState({step: 0})}>Prev</Button.Ripple> */}
                    <Button color="none" className="bg-gradient-success" onClick={() => this.setState({step: 1})}>Next</Button>
                  </div>
                </FormGroup>
              </Col>
            </Row>
          </Form>
        </CardBody>
      </Card>
    :
      <Card>
        <CardHeader>
          <CardTitle>SS Form</CardTitle>
        </CardHeader>
        <CardBody>
          <Form>
            <Row>
              <Col sm="12">
                <FormGroup>
                  <Label for="">Pilih Atasan</Label>
                  <Select
                    className="React"
                    classNamePrefix="select"
                    // defaultValue={colourOptions[1]}
                    name="clear"
                    options={this.state.dataAtasan}
                    isClearable={true}
                    onChange={(val)=>this.setState({ssAtasanSelected: val.value})}
                    // onChange={(val)=>this.setState({ssAtasanSelected: val.target.value})}
                  />
                </FormGroup>
              </Col>
              <Col sm="12">
                <FormGroup>
                  <Label for="ssTema">Tema</Label>
                  <Input
                    type="text"
                    name="ssTema"
                    id="ssTema"
                    placeholder="Tema"
                    value={this.state.ssTema}
                    onChange={(val)=>this.setState({ssTema: val.target.value})}
                    required
                  />
                </FormGroup>
              </Col>
              <Col sm="12">
                <FormGroup>
                  <Label for="ssLokasi">Lokasi</Label>
                  <Input
                    type="text"
                    name="ssLokasi"
                    id="ssLokasi"
                    placeholder="Lokasi Suggestion System"
                    value={this.state.ssLokasi}
                    onChange={(val)=>this.setState({ssLokasi: val.target.value})}
                    required
                  />
                </FormGroup>
              </Col>
              <Col sm="12">
                <FormGroup>
                  <Label for="">Kondisi Sebelum Perbaikan</Label>
                  <Input
                    type="textarea"
                    rows="3"
                    id='ssKondisiSebelum'
                    onChange={(val)=>this.setState({ssKondisiSebelum: val.target.value})}
                    value={this.state.ssKondisiSebelum}
                    placeholder={'Jelaskan kondisi sebelum perbaikan'}
                    required
                    />
                </FormGroup>
              </Col>
              <Col sm="12">
                <FormGroup>
                  <Label for="">Kondisi Sesudah Perbaikan</Label>
                  <Input
                    type="textarea"
                    rows="3"
                    id='ssKondisiSesudah'
                    onChange={(val)=>this.setState({ssKondisiSesudah: val.target.value})}
                    value={this.state.ssKondisiSesudah}
                    placeholder={'Jelaskan kondisi setelah perbaikan'}
                    required
                    />
                </FormGroup>
              </Col>
              <Col sm="12">
                <FormGroup>
                  <Label for="">Permasalahan yang ada</Label>
                  <Input
                    type="textarea"
                    rows="3"
                    id='ssPermasalahan'
                    onChange={(val)=>this.setState({ssPermasalahan: val.target.value})}
                    value={this.state.ssPermasalahan}
                    placeholder={'Jelaskan permasalahan yang ada'}
                    required
                    />
                </FormGroup>
              </Col>
              <Col sm="12">
                <FormGroup>
                  <Label for="">Improvement yang dilakukan</Label>
                  <Input
                    type="textarea"
