import React from "react"
import { Link } from "react-router-dom"
import { CardBody, FormGroup, Form, Input, Button, Label } from "reactstrap"
import Checkbox from "../../../../components/@vuexy/checkbox/CheckboxesVuexy"
import { Mail, Lock, Check, Info } from "react-feather"
// import { loginWithJWT, loginWithCustom } from "../../../../redux/actions/auth/loginActions"
import { loginWithCustom } from "../../../../redux/actions/auth/loginActions"
import { connect } from "react-redux"
import { history } from "../../../../history"
import SweetAlert from 'react-bootstrap-sweetalert';
import { useParams } from 'react-router-dom';

class LoginCustom extends React.Component {
  state = {
    // email: "admin@gmail.com",
    // password: "admin",
    nik:"",
    password:"",
    alert: false,
    remember: false
  }

  handleLogin = async(e) => {
    e.preventDefault()
    var res = await this.props.loginWithCustom(this.state)

    // const queryParams = await new URLSearchParams(window.location.search);
    // const name = await queryParams.get('status');
    // console.log(name); // 55 test null

    // console.log('res',this.props.match.params   )
    // this.props.loginWithJWT(this.state)
  }

  handleAlert = (state, value) => {
    this.setState({ [state] : value })
  }

  componentDidMount= async(e) =>{
    // console.log('e :', e)
    // console.log('login props: ', this.props)

    console.log('didmount : ', e)
    const queryParams = await new URLSearchParams(window.location.search);
    const name = await queryParams.get('status');
    console.log(name); // 55 test null

    if (name == 'wrong_password') {
      console.log("SALAH WOY")
      this.setState({alert: true});
    }
  }

  render() {
    return (
      <React.Fragment>
        <CardBody className="pt-1">
          <Form action="/home" onSubmit={this.handleLogin}>
            <FormGroup className="form-label-group position-relative has-icon-left">
              <Input
                type="text"
                placeholder="NIK"
                value={this.state.nik}
                onChange={e => this.setState({ nik: e.target.value })}
                required
              />
              <div className="form-control-position">
                {/* <Mail size={15} /> */}
                <Info size={15} />
              </div>
              <Label>NIK</Label>
            </FormGroup>
            <FormGroup className="form-label-group position-relative has-icon-left">
              <Input
                type="password"
                placeholder="Password"
                value={this.state.password}
                onChange={e => this.setState({ password: e.target.value })}
                required
              />
              <div className="form-control-position">
                <Lock size={15} />
              </div>
              <Label>Password</Label>
            </FormGroup>
            {/* <FormGroup className="d-flex justify-content-between align-items-center">
              <Checkbox
                color="primary"
                icon={<Check className="vx-icon" size={16} />}
                label="Remember me"
                defaultChecked={false}
                onChange={this.handleRemember}
              />
              <div className="float-right">
                <Link to="/pages/forgot-password">Forgot Password?</Link>
              </div>
            </FormGroup> */}
            {/* <div className="d-flex justify-content-between">
              <Button.Ripple
                color="primary"
                outline
                onClick={() => {
                  history.push("/pages/register")
                }}
              >
                Register
              </Button.Ripple>
              <Button.Ripple color="primary" type="submit">
                Login
              </Button.Ripple>
            </div> */}
            <div className="d-flex justify-content-end">
              <Button.Ripple color="primary" type="submit">
                Login
              </Button.Ripple>
            </div>
          </Form>

          <SweetAlert error title="Error"
              show={this.state.alert}
              onConfirm={() => this.handleAlert("alert", false)}
            >
                <p className="sweet-alert-text">
                  You clicked the button!
                </p>
            </SweetAlert>
        </CardBody>
      </React.Fragment>
    )
  }
}
const mapStateToProps = state => {
  return {
    values: state.auth.login
  }
}
export default connect(mapStateToProps, {
  // loginWithJWT,
  loginWithCustom
})(LoginCustom)
