import React from "react"
import { api_query } from "../../../api/ApiConstant"
import {
  Card,
  CardBody,
  CardHeader,
  CardTitle,
  Badge,
  Input,
  Button,
  Label, Modal, ModalHeader, ModalBody, ModalFooter, Alert
} from "reactstrap"
import DataTable from "react-data-table-component"
import { Star, Search,
    Edit,
    Trash,
    ChevronDown,
    Plus,
    Check,
    ChevronLeft,
    ChevronRight } from "react-feather"
import axios from 'axios'
import { history } from "../../../../src/history"
import Spinner from "../../../components/@vuexy/spinner/Fallback-spinner"


const CustomHeader = props => {
  return (
    // <div className="d-flex flex-wrap justify-content-between">
    <div className="d-flex flex-wrap justify-content-end">
      {/* <div className="add-new">
        <Button.Ripple color="none" className="bg-gradient-success" onClick={() => props.tarikData()}>Add New</Button.Ripple>
      </div> */}
      <div className="position-relative has-icon-left mb-1">
        <Input value={props.value} onChange={e => props.handleFilter(e)} />
        <div className="form-control-position">
          <Search size="15" />
        </div>
      </div>
    </div>
  )
}

const ApprovalComponent = props => {
    return (
      <div className="data-list-action">
        <Button.Ripple className="mr-1 bg-gradient-success" color="none" size="sm"
          onClick={()=> {
            console.log('props : ', props);
            console.log('digimon : ', props.row.name);
            return props.isVisible(props.row.name, props.row.img)
          }}> Approve</Button.Ripple>
      </div>
    )
  }

  const RejectComponent = props => {
    return (
      <div className="data-list-action">
        <Button.Ripple className="mr-1 bg-gradient-danger" color="none" size="sm"
          onClick={()=> {
            console.log('props : ', props);
            console.log('digimon : ', props.row.name);
            return props.isVisible(props.row.name, props.row.img)
          }}> Reject</Button.Ripple>
      </div>
    )
  }

  const ActionsComponent = props => {
    return (
      <div className="data-list-action">
        <Button.Ripple className="mr-1 bg-gradient-success" color="none" size="sm"
          onClick={()=> {
            // console.log('props : ', props);
            return props.isView(props.row, props.row.id, props.row.cff_id)
          }}>View</Button.Ripple>
      </div>
    )
  }



class SpvApprovalSSTable extends React.Component {
  state = {
    modalApproval: false,
    dataApproval: [],
    columnsApproval: [
      {
        name: "Nama",
        selector: "nama",
        sortable: true,
        minWidth: "200px",
        cell: row =>
        (
          <div className="d-flex flex-xl-row flex-column align-items-xl-center align-items-start py-xl-0 py-1">
            <div className="user-info text-truncate ml-xl-50 ml-0">
              <span
                title={row.name}
                className="d-block text-bold-500 text-truncate mb-0">
                {row.name}
                {/* {row.tema} */}
              </span>
            </div>
          </div>
        )
      },
      {
        name: "Status",
        selector: "approval_rejected_status",
        sortable: true,
        minWidth: "200px",
        cell: row => (
          <div className="d-flex flex-xl-row flex-column align-items-xl-center align-items-start py-xl-0 py-1">
            <div className="user-info text-truncate ml-xl-50 ml-0">
              <span
                title={row.approval_rejected_status}
                className="d-block text-bold-500 text-truncate mb-0">
                {row.approval_rejected_status}
              </span>
            </div>
          </div>
        )
      },
      {
        name: "Tanggal",
        selector: "approve_date",
        sortable: true,
        minWidth: "200px",
        cell: row => (
          <div className="d-flex flex-xl-row flex-column align-items-xl-center align-items-start py-xl-0 py-1">
            <div className="user-info text-truncate ml-xl-50 ml-0">
              <span
                title={row.approve_date}
                className="d-block text-bold-500 text-truncate mb-0">
                {row.approve_date}
              </span>
            </div>
          </div>
        )
      },
    ],
    isLoading: false,
    modalVisible: false,
    modalImage: '',
    modalDigimonName: '',
    myProfile: null,
    columns: [
      {
        name: "Tema",
        selector: "tema",
        sortable: true,
        minWidth: "200px",
        cell: row =>
        (
          <div className="d-flex flex-xl-row flex-column align-items-xl-center align-items-start py-xl-0 py-1">
            <div className="user-info text-truncate ml-xl-50 ml-0">
              <span
                title={row.tema_singkat}
                className="d-block text-bold-500 text-truncate mb-0">
                {row.tema_singkat}
                {/* {row.tema} */}
              </span>
            </div>
          </div>
        )
      },
      {
        name: "Tanggal dibuat",
        selector: "tgl",
        sortable: true,
        minWidth: "200px",
        cell: row => (
          <div className="d-flex flex-xl-row flex-column align-items-xl-center align-items-start py-xl-0 py-1">
            <div className="user-info text-truncate ml-xl-50 ml-0">
              <span
                title={row.tgl}
                className="d-block text-bold-500 text-truncate mb-0">
                {row.tgl}
              </span>
            </div>
          </div>
        )
      },
      {
        name: "Status",
        selector: "status_deskripsi",
        sortable: true,
        minWidth: "200px",
        cell: row => (
          <div className="d-flex flex-xl-row flex-column align-items-xl-center align-items-start py-xl-0 py-1">
            <div className="user-info text-truncate ml-xl-50 ml-0">
              <a onClick={()=>this.viewApproval(row)}>
                <span
                  title={row.status_deskripsi}
                  className="d-block text-bold-500 text-truncate mb-0"
                  >
                  {row.status_deskripsi}
                </span>
              </a>
            </div>
          </div>
        )
      },
