}

  render() {
    // const [centeredModal, setCenteredModal] = useState(false)
    return (
      <>
    {this.state.isLoading?
          <Spinner/>
      :
          <div>
            <Button.Ripple color="none" className="bg-gradient-warning mb-1" onClick={() => this.props.changeViewPage()}>Back</Button.Ripple>
