      //   console.log('state keuntungan : ', this.state.ssKeuntungan)

      await this.setState({
        isLoading: false
      });
      console.log('props edit : ', this.props);
    };

    this.onImageChange = async event => {
      this.setState({
        fotoKondisiSebelumFile: event.target.files
      });

      if (event.target.files && event.target.files[0]) {
        let reader = new FileReader();

        reader.onload = e => {
          this.setState({
            fotoKondisiSebelum: e.target.result
          });
        };

        reader.readAsDataURL(event.target.files[0]);
      }
    };

    this.onImageChange2 = async event => {
      this.setState({
        fotoKondisiSesudahFile: event.target.files
      });

      if (event.target.files && event.target.files[0]) {
        let reader = new FileReader();

        reader.onload = e => {
          this.setState({
