import React, { useState } from "react"
import { api_query } from "../../../../api/ApiConstant"
import axios from 'axios'
import {
  Card,
  CardHeader,
  CardTitle,
  CardBody,
  FormGroup,
  Row,
  Col,
  Input,
  Form,
  Button,
  CardImg,
  CustomInput,
  Label, Modal, ModalHeader, ModalBody, ModalFooter, Alert
} from "reactstrap"
import Checkbox from "../../../../components/@vuexy/checkbox/CheckboxesVuexy"
import { Check, Star, Save, Send, X, RotateCcw } from "react-feather"
import { history } from "../../../../../src/history"
import Select from "react-select"
import SweetAlert from "react-bootstrap-sweetalert"
import Toggle from "react-toggle"
import "react-toggle/style.css"
import "../../../../assets/scss/plugins/forms/switch/react-toggle.scss"
import Spinner from "../../../../components/@vuexy/spinner/Fallback-spinner"
import img1 from "../../../../assets/img/pages/1.png"
import img2 from "../../../../assets/img/pages/2.png"
import logo from "../../../../assets/img/logo/dankos-logo.png"
import { PDFDownloadLink, Document, Page, View, Text, Link, Font, StyleSheet, Image } from '@react-pdf/renderer';

const styles = StyleSheet.create({
    title: {
      // margin: 20,
      marginTop: 30,
      marginLeft: 20,
      marginRight: 20,
      fontSize: 20,
      textAlign: 'center',
      // backgroundColor: '#e4e4e4',
      fontWeight: 'bold',
      textDecoration: 'underline'
      // fontFamily: 'Oswald',
      // textTransform: 'uppercase',
      // fontFamily: 'Oswald',
    },
    image: {
      marginLeft: 40,
      marginTop: -20,
      // width: 50, //kesamping
      height: 35 //kebawah
    },
    body: {
      flexGrow: 1,
    },
    row: {
      // flexGrow: 1,
      flexDirection: 'row',
    },
    row2: {
      flexGrow: 1,
      // marginLeft: 20,
      // marginRight: 20,
      flexDirection: 'row',
      // border: '2px solid rgba(0, 0, 0, 0.05)', 
    },
    row3: {
      // flexGrow: 1,
      // marginLeft: 20,
      // marginRight: 20,
      flexDirection: 'row',
    },
    border: {
      border: '2px solid rgba(0, 0, 0, 0.05)', 
