import React from "react"
import {
  Card,
  CardBody,
  CardHeader,
  CardTitle,
  Badge,
  Input,
  Button
} from "reactstrap"
import DataTable from "react-data-table-component"
import { Star, Search, 
    Edit,
    Trash,
    ChevronDown,
    Plus,
    Check,
    ChevronLeft,
    ChevronRight } from "react-feather"
import axios from 'axios'
import { api_query } from "../../api/ApiConstant"
import { Link, NavLink } from "react-router-dom"

const CustomHeader = props => {
  return (
    <div className="d-flex flex-wrap justify-content-between">
      <div className="add-new">
        <Button.Ripple color="none" className="bg-gradient-success" onClick={() => props.employeesAddPage()}>Add New</Button.Ripple>
        {/* <Link to="/">
          <Button color="none" className="bg-gradient-success">Pindah</Button>
        </Link> */}
      </div>
      <div className="position-relative has-icon-left mb-1">
        <Input value={props.value} onChange={e => props.handleFilter(e)} />
        <div className="form-control-position">
          <Search size="15" />
        </div>
      </div>
    </div>
  )
}

const ActionsComponent = props => {
    return (
      <div className="data-list-action">
        <Button.Ripple className="mr-1 mb-1 bg-gradient-success" color="none" 
          onClick={()=> { console.log('DO SOMETHING') }}> Edit </Button.Ripple>

      </div>
    )
  }

class DataTableDigimon extends React.Component {
  state = {
    columns: [
      {
        name: "Name",
        selector: "name",
        sortable: true,
        minWidth: "200px",
        cell: row => (
          <div className="d-flex flex-xl-row flex-column align-items-xl-center align-items-start py-xl-0 py-1">
            <div className="user-info text-truncate ml-xl-50 ml-0">
              <span
                title={row.first_name+' '+row.last_name}
                className="d-block text-bold-500 text-truncate mb-0">
                {row.first_name} {row.last_name}
              </span>
            </div>
          </div>
        )
      },
      {
        name: "Gender",
        selector: "gender",
        sortable: true,
        minWidth: "200px",
        cell: row => (
          <div className="d-flex flex-xl-row flex-column align-items-xl-center align-items-start py-xl-0 py-1">
            <div className="user-info text-truncate ml-xl-50 ml-0">
              <span
                title={row.gender == 'M' ? 'Laki - Laki' : 'Perempuan'}
                className="d-block text-bold-500 text-truncate mb-0">
                {row.gender == 'M' ? 'Laki - Laki' : 'Perempuan'}
              </span>
            </div>
          </div>
        )
      },{
        name: "No Karyawan",
        selector: "emp_no",
        sortable: true,
        minWidth: "200px",
        cell: row => (
          <div className="d-flex flex-xl-row flex-column align-items-xl-center align-items-start py-xl-0 py-1">
            <div className="user-info text-truncate ml-xl-50 ml-0">
              <span
                title={row.emp_no}
                className="d-block text-bold-500 text-truncate mb-0">
                {row.emp_no}
              </span>
            </div>
          </div>
        )
      },
      {
        name: "Actions",
        sortable: true,
        cell: row => (
          <ActionsComponent
            row={row}
          />
        )
      }
    ],
    data: [],
    filteredData: [],
    value: ""
  }

  componentDidMount=async()=>{
    
    await this.tarikData();
    // console.log('state data:', typeof(this.state.data))

  }

  componentDidUpdate=async(prevProps, prevState, snapShot)=>{
    console.log('prevProps', prevProps)
    console.log('prevState', prevState.data.length)
  }


  tarikData = async() =>{
    console.log('tarik data')
    let sql = `
        select * from employees_new limit 1001;
    `
    var result = await axios.post(api_query, {
                                query : sql
                            })
                                .then(result => {
                                let res = result.data.response
                                console.log('result : ', result)
                                if (res.length == '') {
                                    return []
                                }
                                // console.log('result : ', (result.data))
                                return res;
                            }).catch(err => {
                                console.log('error : ', err)
                                return []
                            })
    await this.setState({data: result})
    }

  employeesAddPage = async() =>{
      // console.log('props', this.props)
      await this.props.changeViewPage('add')
  }

  handleFilter = e => {
    let value = e.target.value
    let data = this.state.data
    let filteredData = this.state.filteredData
    this.setState({ value })

    if (value.length) {
      filteredData = data.filter(item => {
        
        var all_name = item.first_name + ' ' + item.last_name
        // console.log('ITEM : ', item)
        let startsWithCondition =
          item.first_name.toLowerCase().startsWith(value.toLowerCase()) ||
          item.last_name.toLowerCase().startsWith(value.toLowerCase()) ||
          all_name.toLowerCase().startsWith(value.toLowerCase()) ||
          item.emp_no.toString().toLowerCase().startsWith(value.toLowerCase())
          // item.name.toLowerCase().startsWith(value.toLowerCase())
          // item.date.toLowerCase().startsWith(value.toLowerCase()) ||
          // item.email.toLowerCase().startsWith(value.toLowerCase()) ||
          // item.revenue.toLowerCase().startsWith(value.toLowerCase()) ||
          // item.status.toLowerCase().startsWith(value.toLowerCase())
        let includesCondition =
          item.first_name.toLowerCase().includes(value.toLowerCase()) ||
          item.last_name.toLowerCase().includes(value.toLowerCase()) ||
          all_name.toLowerCase().startsWith(value.toLowerCase()) ||
          item.emp_no.toString().toLowerCase().includes(value.toLowerCase())
          // item.name.toLowerCase().includes(value.toLowerCase())
          // item.date.toLowerCase().includes(value.toLowerCase()) ||
          // item.email.toLowerCase().includes(value.toLowerCase()) ||
          // item.revenue.toLowerCase().includes(value.toLowerCase()) ||
          // item.status.toLowerCase().includes(value.toLowerCase())

        if (startsWithCondition) {
          return startsWithCondition
        } else if (!startsWithCondition && includesCondition) {
          return includesCondition
        } else return null
      })
      this.setState({ filteredData })
    }
  }

  render() {
    let { data, columns, value, filteredData } = this.state
    return (
        <>
            <Card>
                <CardHeader>
                <CardTitle>Employees Table</CardTitle>
                </CardHeader>
                <CardBody className="rdt_Wrapper">
                <DataTable
                    className="dataTable-custom"
                    data={value.length ? filteredData : data}
                    columns={columns}
                    noHeader
                    pagination
                    subHeader
                    subHeaderComponent={
                    <CustomHeader value={value} handleFilter={this.handleFilter} employeesAddPage={this.employeesAddPage}/>
                    }
                />
                </CardBody>
            </Card>

      </>
    )
  }
}

export default DataTableDigimon
